import React from 'react';
import './App.scss';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import SearchIcon  from '@material-ui/icons/Search';
import IconButton from '@material-ui/core/IconButton';
import axios from 'axios';

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      searchInputFilm:'',
      filmList:[],
      showFilmList:false
    };
    this.handleChange = this.handleChange.bind(this);
    this.seachAction = this.seachAction.bind(this);
  }
  handleChange(event) {
    const filmInputValue=event.target.value
    console.log(filmInputValue)
    this.setState({searchInputFilm: filmInputValue});
  }
  seachAction(){

    axios.get('https://api.tvmaze.com/search/shows?q='+this.state.searchInputFilm)
      .then(res => {
        const filmListApi = res.data;
        this.setState({
           filmList:filmListApi,
           showFilmList:true
          });
      })
  }
  componentDidMount() {
  }
  render(){
    const {searchInputFilm,showFilmList}=this.state
    return ( 
      <div className = "App" >
      <div className="navbar">
        <Container fixed >
        < img className = "img-fluid"
        alt = "logo brand nobo noir"
        src = {require('./images/nobo-logo.png')} /> 
        </Container> 
      </div>

      <Container fixed className="content">
        <div className="content-search">
          <div className="search-film">
          <Grid container spacing={1} alignItems="flex-end">
            <Grid item>
              <TextField id="input-with-icon-grid" value={searchInputFilm} onChange={this.handleChange} label="Film Name" />
            </Grid>
            <Grid item>
            <IconButton className='icon-button' aria-label="search" onClick={this.seachAction} >
              <SearchIcon />
            </IconButton>
            </Grid>
          </Grid>
        </div>

       </div>
      </Container>
      <div className="footer">
        <Container fixed className="footer-content" >
        < img className = "logo-footer"
        alt = "logo brand nobo blanc"
        src = {require('./images/nobo-logo-white.svg')} /> 
        <p>Test Technique Nobo</p>
        </Container> 
      </div>
      </div>
    );
  }

}

export default App;
