import React, { Component } from 'react';
import axios from "axios";
import 'react-big-scheduler/lib/css/style.css'
import 'react-big-calendar/lib/css/react-big-calendar.css'
import moment from 'moment'
import BigCalendar from 'react-big-calendar'

import {
  Modal, Button
} from 'react-bootstrap';

const localizer = BigCalendar.momentLocalizer(moment);

class Calendar extends Component {
  state = {
    appointments: [],
    modal: false,
    selectedEvent: {},
    startDate: new Date(),
    endDate: new Date()
  }
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.onSelectEvent = this.onSelectEvent.bind(this);
  }
  onSelectEvent(event, e) {
    this.setState(prevState => ({
      modal: !prevState.modal,
      selectedEvent: {
        name: event.title,
        description: event.description
      },
      startDate: event.start,
      endDate: event.end
    }));
    console.log(event);
  }
  toggle() {
    console.log('dkhlt')
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }
  async initDoctors() {
    const id = 0
    //const res = await axios.get(`http://localhost:8081/doctor/show/${id}`);
    const res = await axios.get(`http://localhost:8081/doctor/show/-M9_5pj1A-BkUQgiqRvO`);
    return res.data
  }
  async componentDidMount() {

    const appointments = [];
    const doctor = await this.initDoctors()
    const appointmentArray = Object.keys(res.data).map((e) => {
      return {
        id: e,
        ...res.data[e]
      }

    })
    if(doctor.appointments){
      doctor.appointments.map(appointment=>{
        let e = {
          id: doctor.phone,
          title: 'test',
          //title: doctor.firstName,
          description: 'test',
          description: doctor.speciality,
          start: new Date(appointment.date),
          end: new Date(appointment.date)
        }
        appointments.push(e);
      })
      this.setState({
        appointments: appointments
      })
    }
    
  }



  render() {
    return (
      <div className="animated fadeIn">


        <BigCalendar
          localizer={localizer}
          events={this.state.evt}
          defaultView='month'
          views={['month', 'week', 'day']}
          defaultDate={new Date()}
          onSelectEvent={this.onSelectEvent}
        />
        <Modal show={this.state.modal} onHide={this.toggle}>
          <Modal.Header closeButton>
            <Modal.Title>{this.state.selectedEvent.name}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {this.state.selectedEvent.description}
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => { this.toggle() }}>
              Close
          </Button>
          </Modal.Footer>
        </Modal>
      </div>)


  }
}


//export default withDragDropContext(Calendar);
export default (Calendar);
