import React from 'react';
import './App.scss';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import SearchIcon  from '@material-ui/icons/Search';
class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      searchInputFilm:''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    const filmInputValue=event.target.value
    console.log(filmInputValue)
    this.setState({searchInputFilm: filmInputValue});
  }
  seachAction(){
    console.log("hello")
  }
  componentDidMount() {
  }
  render(){
    const {searchInputFilm}=this.state
    return ( 
      <div className = "App" >
      <div className="navbar">
        <Container fixed >
        < img className = "img-fluid"
        alt = "logo brand nobo noir"
        src = {require('./images/nobo-logo.png')} /> 
        </Container> 
      </div>

      <Container fixed className="content">
        <div className="content-search">
        <div className="search-film">
          <Grid container spacing={1} alignItems="flex-end">
            <Grid item>
              <TextField id="input-with-icon-grid" value={searchInputFilm} onChange={this.handleChange} label="Film Name" />
            </Grid>
            <Grid item>
              <SearchIcon onClick={seachAction}  />
            </Grid>
          </Grid>
        </div>
       </div>
      </Container>
      <div className="footer">
        <Container fixed className="footer-content" >
        < img className = "logo-footer"
        alt = "logo brand nobo blanc"
        src = {require('./images/nobo-logo-white.svg')} /> 
        <p>Test Technique Nobo</p>
        </Container> 
      </div>
      </div>
    );
  }

}

export default App;
