import React from 'react';
import './film-details.scss';
class FilmDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            film:this.props.location.prop['film']
        };
    }
    componentDidMount() {
        console.log(this.state.film.genre)
    }
    render() {
        const film = this.state.film
        return (
            <div className="content-search">
                <div className="book">
                    <div className="book-pic">
                        <img src={film.image.medium} alt={film.name} />
                    </div>
                    <div className="book-details">
                    <p><span class="subtitle" >Date de sortie : </span> <span>{film.premiered} </span></p>
                    <p> 
                        <span class="subtitle" >genres : </span>
                        {film.genres.map(g=>(
                            <span> { g } </span> 
                        ))} 
                    </p>
                    <p><span class="subtitle" >Nationalit√©  :  </span> <span>{film.network.country.name} </span></p>
                    <p><span class="subtitle" >Langue  : </span> <span>{film.language} </span></p>
                    <p><span class="subtitle" >Description  : </span><span dangerouslySetInnerHTML={{__html: film.summary}}>  </span></p>     
                    </div>
                </div>
            </div>

        );
    }
}
export default FilmDetails;
